import express, { type Request, Response, NextFunction } from "express";
import { registerRoutes } from "./routes";
import { setupVite, serveStatic, log } from "./vite";

const app = express();
app.use(express.json());
app.use(express.urlencoded({ extended: false }));

app.use((req, res, next) => {
  const start = Date.now();
  const path = req.path;
  let capturedJsonResponse: Record<string, any> | undefined = undefined;

  const originalResJson = res.json;
  res.json = function (bodyJson, ...args) {
    capturedJsonResponse = bodyJson;
    return originalResJson.apply(res, [bodyJson, ...args]);
  };

  res.on("finish", () => {
    const duration = Date.now() - start;
    if (path.startsWith("/api")) {
      let logLine = `${req.method} ${path} ${res.statusCode} in ${duration}ms`;
      if (capturedJsonResponse) {
        logLine += ` :: ${JSON.stringify(capturedJsonResponse)}`;
      }

      if (logLine.length > 80) {
        logLine = logLine.slice(0, 79) + "â€¦";
      }

      log(logLine);
    }
  });

  next();
});

(async () => {
  // Database connection handled by Drizzle ORM in db.ts

  const server = await registerRoutes(app);

  app.use((err: any, _req: Request, res: Response, _next: NextFunction) => {
    const status = err.status || err.statusCode || 500;
    const message = err.message || "Internal Server Error";

    res.status(status).json({ message });
    throw err;
  });

  // importantly only setup vite in development and after
  // setting up all the other routes so the catch-all route
  // doesn't interfere with the other routes
  if (app.get("env") === "development") {
    await setupVite(app, server);
  } else {
    serveStatic(app);
  }

  // ALWAYS serve the app on the port specified in the environment variable PORT
  // Other ports are firewalled. Default to 5000 if not specified.
  // this serves both the API and the client.
  // It is the only port that is not firewalled.
  const port = parseInt(process.env.PORT || '5000', 10);

  // Use different listen options based on environment
  const listenOptions = process.env.REPLIT_DEPLOYMENT 
    ? { port, host: "0.0.0.0", reusePort: true }
    : { port, host: "0.0.0.0" };

  server.listen(listenOptions, () => {
    log(`serving on port ${port}`);
  });
})();

server/openai.ts.content:
import { GoogleGenerativeAI } from "@google/generative-ai";

// Initialize Google Gemini AI
const geminiAPI = process.env.GOOGLE_API_KEY ? new GoogleGenerativeAI(process.env.GOOGLE_API_KEY) : null;
const gemini = geminiAPI ? geminiAPI.getGenerativeModel({ model: "gemini-1.5-flash" }) : null;

interface AISearchResponse {
  content: string;
  sources: Array<{
    title: string;
    url: string;
    snippet: string;
  }>;
}

export async function generateAIResponse(query: string, category?: string): Promise<AISearchResponse> {
  const systemPrompt = `You are an AI assistant that provides accurate, informative responses to user queries. 
    ${category ? `Focus your response on the ${category} category.` : ''}

    Provide a comprehensive answer and include relevant sources. Format your response as JSON with this structure:
    {
      "content": "Your detailed response here",
      "sources": [
        {
          "title": "Source title",
          "url": "https://example.com",
          "snippet": "Brief excerpt from the source"
        }
      ]
    }

    Make sure to:
    1. Provide accurate, factual information
    2. Include 3-5 relevant sources when possible
    3. Keep the response comprehensive but concise
    4. Use proper formatting and structure`;

  // Use Gemini AI
  if (gemini) {
    try {
      const result = await gemini.generateContent([
        systemPrompt,
        `User query: ${query}`
      ]);

      const response = await result.response;
      const text = response.text();

      try {
        // Clean the text by removing markdown code blocks
        const cleanText = text.replace(/```json\n?/g, '').replace(/```\n?/g, '').trim();
        const parsed = JSON.parse(cleanText);
        return {
          content: parsed.content || "I'm sorry, I couldn't generate a response to your query.",
          sources: parsed.sources || []
        };
      } catch {
        // If JSON parsing fails, return the cleaned text as content
        const cleanContent = text.replace(/```json\n?/g, '').replace(/```\n?/g, '').trim();
        return {
          content: cleanContent,
          sources: []
        };
      }
    } catch (error) {
      console.error("Gemini API error:", error);
      // Fall through to fallback
    }
  }

  // Enhanced fallback with useful content based on query
  return {
    content: "I'm sorry, I couldn't generate a response to your query at this time. Please try again later or rephrase your question.",
    sources: []
  };
}

export async function generateSearchSuggestions(query: string): Promise<string[]> {
  const systemPrompt = `Generate 3-5 search suggestions based on the user's partial query. 
  The suggestions should be:
  1. Related to the user's input
  2. Complete, searchable questions or topics
  3. Diverse and covering different angles

  Return only a JSON array of strings in this exact format: ["suggestion 1", "suggestion 2", "suggestion 3"]`;

  // Use Gemini AI
  if (gemini) {
    try {
      const result = await gemini.generateContent([
        systemPrompt,
        `Partial query: "${query}"`
      ]);

      const response = await result.response;
      const text = response.text();

      try {
        // Clean the text by removing markdown code blocks and extra formatting
        const cleanText = text.replace(/```json\n?/g, '').replace(/```\n?/g, '').trim();
        const parsed = JSON.parse(cleanText);
        return Array.isArray(parsed) ? parsed : (parsed.suggestions || []);
      } catch {
        // If JSON parsing fails, extract suggestions manually
        const cleanText = text.replace(/```json\n?/g, '').replace(/```\n?/g, '').trim();
        // Try to extract array-like content
        if (cleanText.includes('[') && cleanText.includes(']')) {
          try {
            const arrayMatch = cleanText.match(/\[.*\]/gs);
            if (arrayMatch) {
              const parsed = JSON.parse(arrayMatch[0]);
              return Array.isArray(parsed) ? parsed : [];
            }
          } catch {}
        }
        // Final fallback - split by lines
        const lines = cleanText.split('\n').filter(line => line.trim().length > 0);
        return lines.slice(0, 3);
      }
    } catch (error) {
      console.error("Gemini search suggestions error:", error);
      // Fall through to fallback
    }
  }

  // Final fallback suggestions
  const fallbackSuggestions = [
    `What is ${query}?`,
    `How does ${query} work?`,
    `Latest news about ${query}`,
    `${query} explained`,
    `Best practices for ${query}`
  ];

  return fallbackSuggestions.slice(0, 3);
}

export async function categorizeQuery(query: string): Promise<string | null> {
  const systemPrompt = `Categorize the following query into one of these categories:
  - Finance
  - Travel
  - Shopping
  - Academic
  - Technology
  - Health
  - null (if it doesn't fit any category)

  Respond with only the category name as a single word, or "null" if it doesn't fit any category.`;

  // Use Gemini AI
  if (gemini) {
    try {
      const result = await gemini.generateContent([
        systemPrompt,
        `Query to categorize: ${query}`
      ]);

      const response = await result.response;
      const text = response.text().trim().toLowerCase();

      // Check if the response matches our categories
      const validCategories = ['finance', 'travel', 'shopping', 'academic', 'technology', 'health'];
      if (validCategories.includes(text)) {
        return text.charAt(0).toUpperCase() + text.slice(1);
      }
      return null;
    } catch (error) {
      console.error("Gemini categorization error:", error);
      // Fall through to fallback
    }
  }

  return null;
}

server/replitAuth.ts.content:
import * as client from "openid-client";
import { Strategy, type VerifyFunction } from "openid-client/passport";

import passport from "passport";
import session from "express-session";
import type { Express, RequestHandler } from "express";
import memoize from "memoizee";
import connectPg from "connect-pg-simple";
import { storage } from "./storage";

const domains = process.env.REPLIT_DOMAINS;
if (!domains && process.env.NODE_ENV !== "production") {
  throw new Error("Environment variable REPLIT_DOMAINS not provided");
}

const getOidcConfig = memoize(
  async () => {
    return await client.discovery(
      new URL(process.env.ISSUER_URL ?? "https://replit.com/oidc"),
      process.env.REPL_ID!
    );
  },
  { maxAge: 3600 * 1000 }
);

export function getSession() {
  const sessionTtl = 7 * 24 * 60 * 60 * 1000; // 1 week
  const pgStore = connectPg(session);
  const sessionStore = new pgStore({
    conString: process.env.DATABASE_URL,
    createTableIfMissing: false,
    ttl: sessionTtl,
    tableName: "sessions",
  });

  // Generate a secure session secret if not provided
  const sessionSecret = process.env.SESSION_SECRET || 'dev-session-secret-' + Math.random().toString(36).substring(2, 15) + Math.random().toString(36).substring(2, 15);

  return session({
    secret: sessionSecret,
    store: sessionStore,
    resave: false,
    saveUninitialized: false,
    cookie: {
      httpOnly: true,
      secure: process.env.NODE_ENV === 'production',
      maxAge: sessionTtl,
    },
  });
}

function updateUserSession(
  user: any,
  tokens: client.TokenEndpointResponse & client.TokenEndpointResponseHelpers
) {
  user.claims = tokens.claims();
  user.access_token = tokens.access_token;
  user.refresh_token = tokens.refresh_token;
  user.expires_at = user.claims?.exp;
}

async function upsertUser(
  claims: any,
) {
  await storage.upsertUser({
    id: claims["sub"],
    email: claims["email"],
    firstName: claims["first_name"],
    lastName: claims["last_name"],
    profileImageUrl: claims["profile_image_url"],
  });
}

export async function setupAuth(app: Express) {
  app.set("trust proxy", 1);
  app.use(getSession());
  app.use(passport.initialize());
  app.use(passport.session());

  const config = await getOidcConfig();

  const verify: VerifyFunction = async (
    tokens: client.TokenEndpointResponse & client.TokenEndpointResponseHelpers,
    verified: passport.AuthenticateCallback
  ) => {
    const user = {};
    updateUserSession(user, tokens);
    await upsertUser(tokens.claims());
    verified(null, user);
  };

  const allowedOrigins = domains ? domains.split(",").map(domain => domain.trim()) : [];
  for (const domain of allowedOrigins) {
    const strategy = new Strategy(
      {
        name: `replitauth:${domain}`,
        config,
        scope: "openid email profile offline_access",
        callbackURL: `https://${domain}/api/callback`,
      },
      verify,
    );
    passport.use(strategy);
  }

  passport.serializeUser((user: Express.User, cb) => cb(null, user));
  passport.deserializeUser((user: Express.User, cb) => cb(null, user));

  app.get("/api/login", (req, res, next) => {
    passport.authenticate(`replitauth:${req.hostname}`, {
      prompt: "login consent",
      scope: ["openid", "email", "profile", "offline_access"],
    })(req, res, next);
  });

  app.get("/api/callback", (req, res, next) => {
    passport.authenticate(`replitauth:${req.hostname}`, {
      successReturnToOrRedirect: "/",
      failureRedirect: "/api/login",
    })(req, res, next);
  });

  app.get("/api/logout", (req, res) => {
    req.logout(() => {
      res.redirect(
        client.buildEndSessionUrl(config, {
          client_id: process.env.REPL_ID!,
          post_logout_redirect_uri: `${req.protocol}://${req.hostname}`,
        }).href
      );
    });
  });
}

export const isAuthenticated: RequestHandler = async (req, res, next) => {
  const user = req.user as any;

  if (!req.isAuthenticated() || !user.expires_at) {
    return res.status(401).json({ message: "Unauthorized" });
  }

  const now = Math.floor(Date.now() / 1000);
  if (now <= user.expires_at) {
    return next();
  }

  const refreshToken = user.refresh_token;
  if (!refreshToken) {
    res.status(401).json({ message: "Unauthorized" });
    return;
  }

  try {
    const config = await getOidcConfig();
    const tokenResponse = await client.refreshTokenGrant(config, refreshToken);
    updateUserSession(user, tokenResponse);
    return next();
  } catch (error) {
    res.status(401).json({ message: "Unauthorized" });
    return;
  }
};

server/routes.ts.content:
import type { Express } from "express";
import { createServer, type Server } from "http";
import { storage } from "./storage";
import { setupAuth, isAuthenticated } from "./replitAuth";
import { generateAIResponse, generateSearchSuggestions } from "./openai";
import { insertSearchSchema, insertConversationSchema, insertMessageSchema } from "@shared/schema";

export async function registerRoutes(app: Express): Promise<Server> {
  // Auth middleware
  await setupAuth(app);

  // Auth routes
  app.get('/api/auth/user', isAuthenticated, async (req: any, res) => {
    try {
      const userId = (req.user as any).claims.sub;
      const user = await storage.getUser(userId);
      res.json(user);
    } catch (error) {
      console.error("Error fetching user:", error);
      res.status(500).json({ message: "Failed to fetch user" });
    }
  });

  // Search routes
  app.post('/api/search', async (req, res) => {
    try {
      const { query, category } = req.body;

      if (!query) {
        return res.status(400).json({ message: "Query is required" });
      }

      console.log(`Processing search query: "${query}" with category: ${category}`);

      const aiResponse = await generateAIResponse(query, category);
      console.log(`AI response generated:`, { 
        contentLength: aiResponse.content?.length || 0, 
        sourcesCount: aiResponse.sources?.length || 0 
      });

      // Ensure we have a valid response
      if (!aiResponse.content) {
        console.warn("No AI content generated, using fallback");
        aiResponse.content = `I apologize, but I wasn't able to generate a response for "${query}". This might be due to configuration issues with the AI services. Please check the server logs or try again later.`;
      }

      const search = await storage.createSearch({
        query,
        response: aiResponse.content,
        category: category || null,
        sources: aiResponse.sources,
        userId: (req.user as any)?.claims?.sub || null,
      });

      // Add to user's search history if authenticated
      if ((req.user as any)?.claims?.sub) {
        await storage.addToSearchHistory((req.user as any).claims.sub, search.id);
      }

      res.json({
        searchId: search.id,
        query,
        response: aiResponse.content,
        sources: aiResponse.sources || [],
        category: category || null,
      });
    } catch (error) {
      console.error("Search error:", error);
      res.status(500).json({ message: "Search failed" });
    }
  });

  app.get('/api/search/suggestions', async (req, res) => {
    try {
      const { q } = req.query;

      if (!q || typeof q !== 'string') {
        return res.json({ suggestions: [] });
      }

      const suggestions = await generateSearchSuggestions(q);
      res.json({ suggestions });
    } catch (error) {
      console.error("Suggestions error:", error);
      res.json({ suggestions: [] });
    }
  });

  app.get('/api/search/:id', async (req, res) => {
    try {
      const search = await storage.getSearchById(req.params.id);
      if (!search) {
        return res.status(404).json({ message: "Search not found" });
      }
      res.json(search);
    } catch (error) {
      console.error("Get search error:", error);
      res.status(500).json({ message: "Failed to get search" });
    }
  });

  // User search history
  app.get('/api/search/history', isAuthenticated, async (req: any, res) => {
    try {
      const userId = (req.user as any).claims.sub;
      const searches = await storage.getSearchesByUser(userId, 50);
      res.json(searches);
    } catch (error) {
      console.error("Get search history error:", error);
      res.status(500).json({ message: "Failed to get search history" });
    }
  });

  // Trending topics routes
  app.get('/api/trending', async (req, res) => {
    try {
      const topics = await storage.getTrendingTopics(10);
      res.json(topics);
    } catch (error) {
      console.error("Get trending topics error:", error);
      res.status(500).json({ message: "Failed to get trending topics" });
    }
  });

  app.post('/api/trending/:id/view', async (req, res) => {
    try {
      await storage.incrementTopicViews(req.params.id);
      res.json({ success: true });
    } catch (error) {
      console.error("Increment topic views error:", error);
      res.status(500).json({ message: "Failed to increment views" });
    }
  });

  // Spaces routes
  app.get('/api/spaces', async (req, res) => {
    try {
      const { category } = req.query;
      let spaces;

      if (category && typeof category === 'string') {
        spaces = await storage.getSpacesByCategory(category);
      } else {
        spaces = await storage.getSpaces(10);
      }

      res.json(spaces);
    } catch (error) {
      console.error("Get spaces error:", error);
      res.status(500).json({ message: "Failed to get spaces" });
    }
  });

  // Categories route
  app.get('/api/categories', async (req, res) => {
    try {
      const categories = [
        {
          id: 'finance',
          name: 'Finance',
          description: 'Get insights on markets, investments, and financial planning',
          icon: 'DollarSign',
          color: 'green',
          href: '/finance'
        },
        {
          id: 'travel',
          name: 'Travel',
          description: 'Discover destinations, plan trips, and travel tips',
          icon: 'Plane',
          color: 'blue',
          href: '/travel'
        },
        {
          id: 'shopping',
          name: 'Shopping',
          description: 'Find products, compare prices, and shopping advice',
          icon: 'ShoppingBag',
          color: 'purple',
          href: '/shopping'
        },
        {
          id: 'academic',
          name: 'Academic',
          description: 'Research assistance and educational content',
          icon: 'GraduationCap',
          color: 'orange',
          href: '/academic'
        }
      ];
      res.json(categories);
    } catch (error) {
      console.error("Get categories error:", error);
      res.status(500).json({ message: "Failed to get categories" });
    }
  });

  // Conversation routes
  app.post('/api/conversations', isAuthenticated, async (req: any, res) => {
    try {
      const userId = (req.user as any).claims.sub;
      const validation = insertConversationSchema.safeParse(req.body);

      if (!validation.success) {
        return res.status(400).json({ message: "Invalid conversation data", errors: validation.error.errors });
      }

      const conversation = await storage.createConversation({
        ...validation.data,
        userId,
      });

      res.json(conversation);
    } catch (error) {
      console.error("Create conversation error:", error);
      res.status(500).json({ message: "Failed to create conversation" });
    }
  });

  app.get('/api/conversations', isAuthenticated, async (req: any, res) => {
    try {
      const userId = req.user.claims.sub;
      const conversations = await storage.getConversationsByUser(userId);
      res.json(conversations);
    } catch (error) {
      console.error("Get conversations error:", error);
      res.status(500).json({ message: "Failed to get conversations" });
    }
  });

  app.get('/api/conversations/:id', isAuthenticated, async (req: any, res) => {
    try {
      const { id } = req.params;
      const conversation = await storage.getConversation(id);

      if (!conversation) {
        return res.status(404).json({ message: "Conversation not found" });
      }

      // Check if user owns this conversation
      if (conversation.userId !== (req.user as any).claims.sub) {
        return res.status(403).json({ message: "Access denied" });
      }

      res.json(conversation);
    } catch (error) {
      console.error("Get conversation error:", error);
      res.status(500).json({ message: "Failed to get conversation" });
    }
  });

  // Message routes
  app.post('/api/conversations/:id/messages', isAuthenticated, async (req: any, res) => {
    try {
      const { id: conversationId } = req.params;
      const userId = (req.user as any).claims.sub;

      // Verify conversation exists and user owns it
      const conversation = await storage.getConversation(conversationId);
      if (!conversation || conversation.userId !== userId) {
        return res.status(404).json({ message: "Conversation not found" });
      }

      const validation = insertMessageSchema.safeParse({
        ...req.body,
        conversationId,
      });

      if (!validation.success) {
        return res.status(400).json({ message: "Invalid message data", errors: validation.error.errors });
      }

      const message = await storage.createMessage(validation.data);

      // Update conversation timestamp
      await storage.updateConversation(conversationId, { updatedAt: new Date() });

      res.json(message);
    } catch (error) {
      console.error("Create message error:", error);
      res.status(500).json({ message: "Failed to create message" });
    }
  });

  app.get('/api/conversations/:id/messages', isAuthenticated, async (req: any, res) => {
    try {
      const { id: conversationId } = req.params;
      const userId = (req.user as any).claims.sub;

      // Verify conversation exists and user owns it
      const conversation = await storage.getConversation(conversationId);
      if (!conversation || conversation.userId !== userId) {
        return res.status(404).json({ message: "Conversation not found" });
      }

      const messages = await storage.getMessagesByConversation(conversationId);
      res.json(messages);
    } catch (error) {
      console.error("Get messages error:", error);
      res.status(500).json({ message: "Failed to get messages" });
    }
  });

  // MongoDB Chat Threads API
  app.post('/api/chat/threads', async (req, res) => {
    try {
      const { message, threadId } = req.body;

      if (!message) {
        return res.status(400).json({ message: "Message is required" });
      }

      console.log(`Processing chat message: "${message.substring(0, 100)}..."`);

      // Generate AI response
      const aiResponse = await generateAIResponse(message);

      if (!aiResponse.content) {
        aiResponse.content = `I apologize, but I wasn't able to generate a response for "${message}". Please try again.`;
      }

      let thread;

      if (threadId) {
        // Add to existing thread
        const userMessage = {
          type: 'user',
          content: message,
          timestamp: new Date()
        };

        const aiMessage = {
          type: 'ai',
          content: aiResponse.content,
          timestamp: new Date(),
          sources: aiResponse.sources || []
        };

        // Add user message
        await mongoStorage.addMessageToThread(threadId, userMessage);
        // Add AI response
        thread = await mongoStorage.addMessageToThread(threadId, aiMessage);
      } else {
        // Create new thread
        const title = message.length > 50 ? message.substring(0, 47) + "..." : message;

        const firstUserMessage = {
          type: 'user',
          content: message,
          timestamp: new Date()
        };

        thread = await mongoStorage.createChatThread(title, firstUserMessage);

        // Add AI response
        const aiMessage = {
          type: 'ai',
          content: aiResponse.content,
          timestamp: new Date(),
          sources: aiResponse.sources || []
        };

        thread = await mongoStorage.addMessageToThread(thread._id.toString(), aiMessage);
      }

      res.json({
        threadId: thread._id.toString(),
        response: aiResponse.content,
        sources: aiResponse.sources || [],
        thread: {
          id: thread._id.toString(),
          title: thread.title,
          messages: thread.messages,
          createdAt: thread.createdAt,
          updatedAt: thread.updatedAt
        }
      });

    } catch (error) {
      console.error("Chat thread error:", error);
      res.status(500).json({ message: "Chat failed" });
    }
  });

  // Get recent chat threads
  app.get('/api/chat/threads', async (req, res) => {
    try {
      const { limit = 10 } = req.query;
      const threads = await mongoStorage.getRecentThreads(parseInt(limit as string));
      res.json(threads);
    } catch (error) {
      console.error("Get chat threads error:", error);
      res.status(500).json({ message: "Failed to get chat threads" });
    }
  });

  // Get specific thread
  app.get('/api/chat/threads/:id', async (req, res) => {
    try {
      const thread = await mongoStorage.getThreadById(req.params.id);
      if (!thread) {
        return res.status(404).json({ message: "Thread not found" });
      }
      res.json(thread);
    } catch (error) {
      console.error("Get thread error:", error);
      res.status(500).json({ message: "Failed to get thread" });
    }
  });

  // Delete thread
  app.delete('/api/chat/threads/:id', async (req, res) => {
    try {
      const success = await mongoStorage.deleteThread(req.params.id);
      if (!success) {
        return res.status(404).json({ message: "Thread not found" });
      }
      res.json({ success: true });
    } catch (error) {
      console.error("Delete thread error:", error);
      res.status(500).json({ message: "Failed to delete thread" });
    }
  });

  // Search threads
  app.get('/api/chat/search', async (req, res) => {
    try {
      const { q, limit = 10 } = req.query;
      if (!q || typeof q !== 'string') {
        return res.json([]);
      }
      const threads = await mongoStorage.searchThreads(q, parseInt(limit as string));
      res.json(threads);
    } catch (error) {
      console.error("Search threads error:", error);
      res.status(500).json({ message: "Failed to search threads" });
    }
  });

  // Legacy chat endpoint for backward compatibility
  app.post('/api/chat', async (req, res) => {
    try {
      const { message, conversationId } = req.body;

      if (!message) {
        return res.status(400).json({ message: "Message is required" });
      }

      let convId = conversationId;

      // If authenticated and no conversation ID, create a new conversation
      if ((req.user as any)?.claims?.sub && !convId) {
        const conversation = await storage.createConversation({
          userId: (req.user as any).claims.sub,
          title: message.length > 50 ? message.substring(0, 50) + "..." : message,
        });
        convId = conversation.id;
      }

      // Generate AI response
      const aiResponse = await generateAIResponse(message);

      if (!aiResponse.content) {
        aiResponse.content = `I apologize, but I wasn't able to generate a response for "${message}". Please try again.`;
      }

      // Save messages if we have a conversation
      if (convId) {
        // Save user message
        await storage.createMessage({
          conversationId: convId,
          role: "user",
          content: message,
        });

        // Save AI response
        await storage.createMessage({
          conversationId: convId,
          role: "assistant", 
          content: aiResponse.content,
          sources: aiResponse.sources,
        });

        // Update conversation timestamp
        await storage.updateConversation(convId, { updatedAt: new Date() });
      }

      res.json({
        conversationId: convId,
        response: aiResponse.content,
        sources: aiResponse.sources || [],
      });

    } catch (error) {
      console.error("Chat error:", error);
      res.status(500).json({ message: "Chat failed" });
    }
  });

  const httpServer = createServer(app);
  return httpServer;
}

server/storage.ts.content:
import {
  users,
  searches,
  trendingTopics,
  spaces,
  searchHistory,
  conversations,
  messages,
  type User,
  type UpsertUser,
  type InsertSearch,
  type Search,
  type InsertTrendingTopic,
  type TrendingTopic,
  type InsertSpace,
  type Space,
  type SearchHistory,
  type InsertConversation,
  type Conversation,
  type InsertMessage,
  type Message,
} from "@shared/schema";
import { randomUUID } from "crypto";

// Interface for storage operations
export interface IStorage {
  // User operations (mandatory for Replit Auth)
  getUser(id: string): Promise<User | undefined>;
  upsertUser(user: UpsertUser): Promise<User>;

  // Search operations
  createSearch(search: InsertSearch): Promise<Search>;
  getSearchById(id: string): Promise<Search | undefined>;
  getSearchesByUser(userId: string, limit?: number): Promise<Search[]>;
  getSearchHistory(userId: string, limit?: number): Promise<SearchHistory[]>;
  addToSearchHistory(userId: string, searchId: string): Promise<SearchHistory>;

  // Conversation operations
  createConversation(conversation: InsertConversation): Promise<Conversation>;
  getConversation(id: string): Promise<Conversation | undefined>;
  getConversationsByUser(userId: string, limit?: number): Promise<Conversation[]>;
  updateConversation(id: string, updates: Partial<Conversation>): Promise<Conversation>;

  // Message operations
  createMessage(message: InsertMessage): Promise<Message>;
  getMessagesByConversation(conversationId: string): Promise<Message[]>;

  // Trending topics operations
  getTrendingTopics(limit?: number): Promise<TrendingTopic[]>;
  createTrendingTopic(topic: InsertTrendingTopic): Promise<TrendingTopic>;
  incrementTopicViews(id: string): Promise<void>;

  // Spaces operations
  getSpaces(limit?: number): Promise<Space[]>;
  createSpace(space: InsertSpace): Promise<Space>;
  getSpacesByCategory(category: string): Promise<Space[]>;
}

export class MemStorage implements IStorage {
  private users: Map<string, User> = new Map();
  private searches: Map<string, Search> = new Map();
  private trendingTopics: Map<string, TrendingTopic> = new Map();
  private spaces: Map<string, Space> = new Map();
  private searchHistoryRecords: Map<string, SearchHistory> = new Map();
  private conversations: Map<string, Conversation> = new Map();
  private messages: Map<string, Message> = new Map();

  constructor() {
    this.seedData();
  }

  private seedData() {
    // Seed trending topics
    const topics: InsertTrendingTopic[] = [
      {
        title: "Latest AI Breakthroughs in 2024",
        description: "Discover the most significant AI developments this year",
        category: "Technology",
        readTime: "2 min read",
        icon: "fas fa-fire",
        viewCount: 1250,
        isActive: true,
      },
      {
        title: "Sustainable Investment Strategies",
        description: "How to build an eco-friendly investment portfolio",
        category: "Finance",
        readTime: "4 min read",
        icon: "fas fa-leaf",
        viewCount: 890,
        isActive: true,
      },
      {
        title: "Hidden Gems in Southeast Asia",
        description: "Off-the-beaten-path destinations for adventurous travelers",
        category: "Travel",
        readTime: "6 min read",
        icon: "fas fa-map-marked-alt",
        viewCount: 567,
        isActive: true,
      },
      {
        title: "Quantum Computing Fundamentals",
        description: "Understanding the basics of quantum computation",
        category: "Academic",
        readTime: "8 min read",
        icon: "fas fa-graduation-cap",
        viewCount: 432,
        isActive: true,
      },
      {
        title: "Best Tech Deals This Week",
        description: "Top technology products with significant discounts",
        category: "Shopping",
        readTime: "3 min read",
        icon: "fas fa-shopping-cart",
        viewCount: 1120,
        isActive: true,
      },
      {
        title: "Mental Health in Remote Work",
        description: "Strategies for maintaining wellbeing while working from home",
        category: "Health",
        readTime: "5 min read",
        icon: "fas fa-heartbeat",
        viewCount: 678,
        isActive: true,
      },
    ];

    topics.forEach((topic) => {
      const id = randomUUID();
      this.trendingTopics.set(id, {
        ...topic,
        id,
        createdAt: new Date(),
      });
    });

    // Seed spaces
    const spacesData: InsertSpace[] = [
      {
        title: "Business Strategy",
        description: "Market analysis, competitive research, and business planning",
        category: "Business",
        templateCount: 12,
        icon: "fas fa-briefcase",
        gradient: "from-blue-500 to-purple-600",
        tags: ["SWOT Analysis", "Market Research"],
        isActive: true,
      },
      {
        title: "Developer Tools",
        description: "Code review, debugging, and technical documentation",
        category: "Technology",
        templateCount: 8,
        icon: "fas fa-code",
        gradient: "from-green-500 to-teal-600",
        tags: ["Code Review", "Documentation"],
        isActive: true,
      },
      {
        title: "Creative Writing",
        description: "Content creation, storytelling, and copywriting assistance",
        category: "Creative",
        templateCount: 15,
        icon: "fas fa-pen-fancy",
        gradient: "from-orange-500 to-red-600",
        tags: ["Blog Posts", "Marketing Copy"],
        isActive: true,
      },
    ];

    spacesData.forEach((space) => {
      const id = randomUUID();
      this.spaces.set(id, {
        ...space,
        id,
        createdAt: new Date(),
      });
    });
  }

  // User operations
  async getUser(id: string): Promise<User | undefined> {
    return this.users.get(id);
  }

  async upsertUser(userData: UpsertUser): Promise<User> {
    const existingUser = Array.from(this.users.values()).find(u => u.id === userData.id);

    if (existingUser) {
      const updatedUser = {
        ...existingUser,
        ...userData,
        updatedAt: new Date(),
      };
      this.users.set(existingUser.id, updatedUser);
      return updatedUser;
    } else {
      const user: User = {
        ...userData,
        id: userData.id || randomUUID(),
        createdAt: new Date(),
        updatedAt: new Date(),
      };
      this.users.set(user.id, user);
      return user;
    }
  }

  // Search operations
  async createSearch(searchData: InsertSearch): Promise<Search> {
    const id = randomUUID();
    const search: Search = {
      ...searchData,
      id,
      createdAt: new Date(),
    };
    this.searches.set(id, search);
    return search;
  }

  async getSearchById(id: string): Promise<Search | undefined> {
    return this.searches.get(id);
  }

  async getSearchesByUser(userId: string, limit = 50): Promise<Search[]> {
    return Array.from(this.searches.values())
      .filter(search => search.userId === userId)
      .sort((a, b) => (b.createdAt?.getTime() || 0) - (a.createdAt?.getTime() || 0))
      .slice(0, limit);
  }

  async getSearchHistory(userId: string, limit = 50): Promise<SearchHistory[]> {
    return Array.from(this.searchHistoryRecords.values())
      .filter(record => record.userId === userId)
      .sort((a, b) => (b.createdAt?.getTime() || 0) - (a.createdAt?.getTime() || 0))
      .slice(0, limit);
  }

  async addToSearchHistory(userId: string, searchId: string): Promise<SearchHistory> {
    const id = randomUUID();
    const record: SearchHistory = {
      id,
      userId,
      searchId,
      createdAt: new Date(),
    };
    this.searchHistoryRecords.set(id, record);
    return record;
  }

  // Trending topics operations
  async getTrendingTopics(limit = 10): Promise<TrendingTopic[]> {
    return Array.from(this.trendingTopics.values())
      .filter(topic => topic.isActive)
      .sort((a, b) => (b.viewCount || 0) - (a.viewCount || 0))
      .slice(0, limit);
  }

  async createTrendingTopic(topicData: InsertTrendingTopic): Promise<TrendingTopic> {
    const id = randomUUID();
    const topic: TrendingTopic = {
      ...topicData,
      id,
      createdAt: new Date(),
    };
    this.trendingTopics.set(id, topic);
    return topic;
  }

  async incrementTopicViews(id: string): Promise<void> {
    const topic = this.trendingTopics.get(id);
    if (topic) {
      topic.viewCount = (topic.viewCount || 0) + 1;
      this.trendingTopics.set(id, topic);
    }
  }

  // Spaces operations
  async getSpaces(limit = 10): Promise<Space[]> {
    return Array.from(this.spaces.values())
      .filter(space => space.isActive)
      .sort((a, b) => (a.createdAt?.getTime() || 0) - (b.createdAt?.getTime() || 0))
      .slice(0, limit);
  }

  async createSpace(spaceData: InsertSpace): Promise<Space> {
    const id = randomUUID();
    const space: Space = {
      ...spaceData,
      id,
      createdAt: new Date(),
    };
    this.spaces.set(id, space);
    return space;
  }

  async getSpacesByCategory(category: string): Promise<Space[]> {
    return Array.from(this.spaces.values())
      .filter(space => space.isActive && space.category === category);
  }

  // Conversation operations
  async createConversation(conversationData: InsertConversation): Promise<Conversation> {
    const id = randomUUID();
    const conversation: Conversation = {
      ...conversationData,
      id,
      createdAt: new Date(),
      updatedAt: new Date(),
    };
    this.conversations.set(id, conversation);
    return conversation;
  }

  async getConversation(id: string): Promise<Conversation | undefined> {
    return this.conversations.get(id);
  }

  async getConversationsByUser(userId: string, limit = 50): Promise<Conversation[]> {
    return Array.from(this.conversations.values())
      .filter(conv => conv.userId === userId)
      .sort((a, b) => (b.updatedAt?.getTime() || 0) - (a.updatedAt?.getTime() || 0))
      .slice(0, limit);
  }

  async updateConversation(id: string, updates: Partial<Conversation>): Promise<Conversation> {
    const conversation = this.conversations.get(id);
    if (!conversation) {
      throw new Error('Conversation not found');
    }
    const updated = {
      ...conversation,
      ...updates,
      updatedAt: new Date(),
    };
    this.conversations.set(id, updated);
    return updated;
  }

  // Message operations
  async createMessage(messageData: InsertMessage): Promise<Message> {
    const id = randomUUID();
    const message: Message = {
      ...messageData,
      id,
      createdAt: new Date(),
    };
    this.messages.set(id, message);
    return message;
  }

  async getMessagesByConversation(conversationId: string): Promise<Message[]> {
    return Array.from(this.messages.values())
      .filter(msg => msg.conversationId === conversationId)
      .sort((a, b) => (a.createdAt?.getTime() || 0) - (b.createdAt?.getTime() || 0));
  }
}

export const storage = new MemStorage();

