            ) : filteredTopics.length > 0 ? (
              <>
                <div className="flex items-center justify-between mb-6">
                  <h2 className="text-xl font-semibold">
                    {filteredTopics.length} topic{filteredTopics.length !== 1 ? 's' : ''} found
