                </div>
                <h3 className="text-lg font-semibold mb-2">No topics found</h3>
                <p className="text-muted-foreground">
                  Try adjusting your search or filter criteria
                </p>
              </div>
            )}
          </div>
        </section>
      </main>
    </Layout>
  );
}

